// seedRaul.js
// Seeder para usuario Raúl: crea el usuario y le agrega entradas de comida, actividad, agua, recetas, comunidad y posteos.

const pool = require('../db');
const bcrypt = require('bcrypt');
const {
  daysAgo,
  addConsumedFood,
  addConsumedWater,
  addDoneActivity,
  createRecipe,
  createCommunity,
  createPost
} = require('./userCreation');

async function seedRaul() {
  // Crear usuario Raúl
  const email = 'mariano@admin.com';
  const username = 'mariano';
  const fullname = 'Mariano Admin';
  const imageUrl = 'data:image/jpeg;base64,'
  const emailVerified = true;
  const rawPassword = 'testeo';
  const hashed = await bcrypt.hash(rawPassword, 10);
  const { rows: userRows } = await pool.query(
    `INSERT INTO users (email, username, password, fullname, registrationDay, image_url, verified, verification_code)
     VALUES ($1, $2, $3, $4, $5, $6, $7, $8)
     RETURNING id`,
    [email, username, hashed, fullname, daysAgo(30), imageUrl, emailVerified, null]
  );
  const userId = userRows[0].id;
  console.log(`Usuario Raúl creado con id=${userId}`);

  const periods = ['desayuno', 'almuerzo', 'merienda', 'cena'];
  const desayunoOptions = [
    'Leche', 'Pan integral', 'Huevos', 'Yogur', 'Jugo de naranja'
  ];
  const almuerzoOptions = [
    'Carne vacuna', 'Carne de cerdo', 'Pasta', 'Pollo', 'Pescado', 'Lechuga', 'Papa'
  ];
  const meriendaOptions = [
    'Yogur', 'Frutilla', 'Naranja', 'Almendras'
  ];
  const cenaOptions = [
    'Carne vacuna', 'Carne de cerdo', 'Pasta', 'Tomate', 'Lentejas', 'Tofu'
  ];
  const periodOptions = [desayunoOptions, almuerzoOptions, meriendaOptions, cenaOptions]

  for (let i = 0; i < periodOptions.length; i ++){
    const foodOptions = periodOptions[i]
    const period = periods[i]
    for (let j = 0; j < 35; j++) {
      const foodName = foodOptions[Math.floor(Math.random() * foodOptions.length)];
      const grams    = Math.floor(Math.random() * 200) + 50;       // 50-249 g
      const consumedAt = daysAgo(Math.floor(Math.random() * 30));  // en el rango 0-29 días atrás
      await addConsumedFood({ userId, foodName, grams, consumedAt, period });
    }
  }
  console.log('✅ 35 * 4 entradas de comida agregadas');

  // 3. Entradas de actividad: 10
  const activityOptions = ['Caminar','Bicicleta', 'Biceps', 'Triceps', 'Hombros', 'Piernas', 'Boxeo', 'HIIT'];
  for (let i = 0; i < 40; i++) {
    const name = activityOptions[Math.floor(Math.random() * activityOptions.length)];

    const result = await pool.query(
    `SELECT type, calories_burn_rate
    FROM activities
    WHERE name = $1
    LIMIT 1`,
        [name]
    );
    if (result.rows.length === 0) {
        return res.status(404).json({ error: 'Actividad no encontrada' });
    }
   
    const { type: type, calories_burn_rate: rate } = result.rows[0];  
    let durationMinutes = null;
    let distanceKm      = null;
    let series          = null;
    let repetitions     = null;
    let caloriesBurned  = null;

    function getRandomIntInclusive(min, max) {
      return Math.floor(Math.random() * (max - min + 1)) + min;
    }
    switch (type) {
      case 'cardio':
        durationMinutes = getRandomIntInclusive(20, 79); // 20–79 min
        distanceKm      = getRandomIntInclusive(1, 7);   // 1–7 km
        caloriesBurned  = rate * durationMinutes;
        break;

      case 'musculacion':
        repetitions    = getRandomIntInclusive(5, 14);  // 5–14 reps
        series         = getRandomIntInclusive(2, 4);   // 2–4 series
        caloriesBurned = rate * repetitions;
        break;

      default:
        console.warn(`Tipo desconocido: ${type}`);
    }
    const performedAt = daysAgo(getRandomIntInclusive(0, 29));
    await addDoneActivity({
      userId,
      activityName    : name,
      durationMinutes,
      distanceKm,
      series,
      repetitions,
      performedAt,
      caloriesBurned
    });
  }
  console.log('✅ 40 entradas de actividad agregadas');

  // Entradas de agua: 30 días, 1.5-2.5 L diarias
  for (let d = 0; d < 30; d++) {
    const consumedAt = daysAgo(d);
    const liters = +(Math.random() * (2.5 - 1.5) + 1).toFixed(2);
    await addConsumedWater({ userId, liters, consumedAt });
  }
  console.log('✅ 30 entradas de agua agregadas para Raúl');

  // Recetas de ejemplo
  const { rows: foods } = await pool.query('SELECT id, name FROM foods');
  const mapId = {};
  foods.forEach(f => { mapId[f.name] = f.id; });

  await createRecipe({
    userId,
    username,
    name: 'Pollo con Arroz',
    items: [
      { foodId: mapId['Pollo'], grams: 100 },
      { foodId: mapId['Arroz'], grams: 50 },
      { foodId: mapId['Aceite de oliva'], grams: 10 }
    ],
    steps: `Cocinar Pollo a la plancha. 
      Cocinar arroz reahogado en aceite. 
      Sazonar al gusto.`,
    pic: 'data:image/jpeg;base64,'
  });

  await createRecipe({
    userId,
    username,
    name: 'Ensalada proteica',
    items: [
      { foodId: mapId['Huevos'], grams: 150 },
      { foodId: mapId['Aceite de oliva'], grams: 10 },
      { foodId: mapId['Lentejas'], grams: 200 },
      { foodId: mapId['Lechuga'], grams: 100 },
    ],
    steps: `Cocinar los Huevos hervidos. 
    Hacer las lentejas. 
    Mezclar todo en un bol con la lechuga.
    Agregar el aceite de oliva.`,
    pic: 'data:image/jpeg;base64,'
  });
  console.log('✅ 2 recetas creadas para Raúl');

  // Comunidad y posteo
  const community = await createCommunity({
    userId,
    name: 'Tips de Entrenamiento',
    description: 'En esta comunidad vas a encontrar las claves para potenciar tu vida al maximo.'
  });
  await createPost({
    userId,
    communityId: community.id,
    title: `Nuevo ejercicio para pectorales`,
    body: 'El press de pecho con bandas elásticas es una alternativa eficaz al press de banca tradicional. Permite trabajar los pectorales en casa sin necesidad de pesas, con menor riesgo articular. Se recomienda controlar el movimiento, mantener buena postura y ajustar la tensión para aumentar la dificultad progresivamente.',
    topic: 'Rutinas de fuerza',
    photos: []
  });
  await createPost({
    userId,
    communityId: community.id,
    title: 'Tuppers semanales',
    body: 'Preparar tuppers con anticipación asegura una alimentación equilibrada, evita decisiones impulsivas y mejora la recuperación post-entrenamiento. Idealmente deben incluir proteínas (pollo, legumbres, tofu), carbohidratos complejos (arroz integral, papa, quinoa) y vegetales. Un día de planificación permite enfocarse mejor en el entrenamiento y la constancia.',
    topic: 'Menu sugerido',
    photos: []
  });

  console.log('✅ Comunidad y posteos creados para Mariano');

  console.log('🎉 Seed Raúl completado.');
}

seedRaul()
  .then(() => process.exit(0))
  .catch(err => { console.error(err); process.exit(1); });
